import React from 'react';
import { Link } from "react-router-dom";
import { TimelineLite } from "gsap";
import Icon from './icons/Index';

class Header extends React.Component{
    constructor(props) {
        super(props);
        this.myTween = new TimelineLite({ paused: true })
        this.state = {
            showMenu: false,
        }
    }

    toggleMenu = () => {
        const menuElement = document.querySelector(".menu-atom");
        // this.myTween
        //     .to(menuElement, .4, {width: 10})
        //     .to(menuElement, 1, {height: '100vh'})
        //     .to(menuElement, 1, {width: '100%'})
        //     .play();
        // TweenLite.to(menuElement, 1, { width: '100%', backgroundColor: "red" });
        // document.querySelector('.menu-atom').classList.toggle('display-none-dft');
        // document.querySelector('.burger-icon').classList.toggle('active');
    }

    render() {
        return (
            <div className={"header-atom"} id='my-element'>
                {/* Logo */}
                <Link to={'/'}>
                    <img className="header-logo" src="./images/logo.svg" alt="logo" />
                </Link>

                {/* Header buttons */}
                <div className="header-buttons">
                    {/* Translate */}
                    <div className="translate">
                        <button>Fr</button> / <button>En</button>
                    </div>

                    {/* Burger menu */}
                    <div className="burger-menu border-rounded-dft" onClick={() => this.toggleMenu()}>
                        <Icon name="Burger" width={30} stroke={"#fff"} />
                    </div>
                </div>
            </div>
        );
    }
}

export default Header;
